import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [youtubeResults, setYoutubeResults] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(''); // Error state

  const handleSearch = async () => {
    // Prevent empty search
    if (!searchTerm.trim()) {
      alert('Please enter a search term');
      return;
    }

    setLoading(true); // Start loading
    setError(''); // Clear previous errors

    try {
      // Fetch YouTube Data
      const youtubeResponse = await axios.get(
        `http://localhost:5000/api/search/youtube`, 
        { params: { q: searchTerm } }
      );
      setYoutubeResults(youtubeResponse.data);

      // Fetch Google Custom Search Data
      const searchResponse = await axios.get(
        `http://localhost:5000/api/search/google`,
        { params: { q: searchTerm } }
      );
      setSearchResults(searchResponse.data);
    } catch (error) {
      console.error('Error fetching data', error);
      setError('Failed to fetch data. Please try again later.');
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div className="app-container">
      <h1 className="app-title">InfoPulse Search</h1>
      <div className="search-container">
        <input
          type="text"
          className="search-input"
          placeholder="Search for videos, articles, papers..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="search-button" onClick={handleSearch}>
          Search
        </button>
      </div>

      {/* Loading Indicator */}
      {loading && <p>Loading...</p>}

      {/* Error Message */}
      {error && <p className="error-message">{error}</p>}

      <div className="results-container">
        <h2>YouTube Results</h2>
        {youtubeResults.length > 0 ? (
          youtubeResults.map((video) => (
            <div key={video.id.videoId} className="result-card">
              <h3>{video.snippet.title}</h3>
              <p>{video.snippet.description}</p>
              <a
                href={`https://www.youtube.com/watch?v=${video.id.videoId}`}
                target="_blank"
                rel="noopener noreferrer"
              >
                Watch Video
              </a>
            </div>
          ))
        ) : (
          !loading && <p>No YouTube results found.</p>
        )}

        <h2>Search Results</h2>
        {searchResults.length > 0 ? (
          searchResults.map((result) => (
            <div key={result.link} className="result-card">
              <h3>{result.title}</h3>
              <p>{result.snippet}</p>
              <a href={result.link} target="_blank" rel="noopener noreferrer">
                Read More
              </a>
            </div>
          ))
        ) : (
          !loading && <p>No search results found.</p>
        )}
      </div>
    </div>
  );
}

export default App;
